            var currentSlide;
            var defaultTabsAmount = thisTabSlider.data('default-tabs-amount');
            var allSlides = $('.js-st-tabs-tab', thisTabSlider);
            var slideAmount = allSlides.length;
            var biggerThanDefaultTabsAmount = (slideAmount > defaultTabsAmount);
